import "./App.css";
import Header from "./components/Header";

function App() {
  //Variable donde guardaremos que apuntes quiere visaulizar el usuario:
  let [visualizar, setVisualizar] = useState("");

  //Acciones para cambiar la visualizaci칩n de los apuntes
  function newRepositorio() {
    setVisualizar("newRepositorio");
  }
  function remote() {
    setVisualizar("remote");
  }
  function acceso() {
    setVisualizar("acceso");
  }
  function pullRequest() {
    setVisualizar("pullRequest");
  }

  const Apuntes = () => {
    return <aside>Nada</aside>;
  };

  //Render condicional
  switch (visualizar) {
    case "":
      return;
      Apuntes = () => {
        return <aside>Nada</aside>;
      };
      break;
    case "newRepositorio":
      return;
      Apuntes = () => {
        return <aside>newRepositorio</aside>;
      };
      break;
    default:
      break;
  }

  return (
    <Fragment className="App">
      {/* <!-- Header begins --> */}
      <Header />
      {/* <!-- Main begins --> */}
      <main>
        {/* /* <!-- Speaker: Estela F. -->  
        <!-- redirected content list -->
        <!-- buttons direcciton contenido--> *idea* */}
        <h1>Apuntes GitHub</h1>
        <section>
          <button onClick={newRepositorio}>Crear Repositorio</button>
          <button onClick={remote}>
            Unir una carpeta local a un repositorio
          </button>
          <button onClick={acceso}>A침adir colaborades en un repositorio</button>
          <button onClick={pullRequest}>
            Pull request. Solicitar un a침adir un cambio
          </button>
        </section>
        <Apuntes />
      </main>

      {/* <!-- Footer begins --> */}
      <footer>
        <p>Footer</p>
      </footer>
    </Fragment>
  );
}

export default App;
